package com.easymi.component.pay;import android.text.TextUtils;import java.util.Map;/** * 支付宝支付结果实体类. */class AlipayResult {    /*      {      "memo" : "xxxxx",      "result" : "{      \"alipay_trade_app_pay_response\":{      \"code\":\"10000\",      \"msg\":\"Success\",      \"app_id\":\"2014072300007148\",      \"out_trade_no\":\"081622560194853\",      \"trade_no\":\"2016081621001004400236957647\",      \"total_amount\":\"0.01\",      \"seller_id\":\"2088702849871851\",      \"charset\":\"utf-8\",      \"timestamp\":\"2016-10-11 17:43:36\"      },      \"sign\":\"NGfStJf3i3ooWBuCDIQSumOpaGBcQz+aoAqyGh3W6EqA/gmyPYwLJ2REFijY9XPTApI9YglZyMw+ZMhd3kb0mh4RAXMrb6mekX4Zu8Nf6geOwIa9kLOnw0IMCjxi4abDIfXhxrXyj********\",      \"sign_type\":\"RSA2\"      }",      "resultStatus" : "9000"      }     */    private String resultStatus;    private String result;    private String memo;    AlipayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, "resultStatus")) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, "result")) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, "memo")) {                memo = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "}";    }    /**     * @return the resultStatus.     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo.     */    public String getMemo() {        return memo;    }    /**     * @return the result.     */    public String getResult() {        return result;    }}